# open earthquakes file in read modefn = open('earthquakes-total-2016-over-6-without-titles.csv', 'r')# open output files in write modefnOutput = open('earthquakes-total-2016-over-6-without-titles-SplitDateTime.csv', 'w')# read the content of the file line by line cont = fn.readlines() type(cont) for line in range(0, len(cont)):       lineText = cont[line]       # create a new line by formatting the text of the current line       newLineText = lineText[0:4] + "," + lineText[0:10] + "," + lineText[11:19]  + "," + lineText       # write the line in the new file       fnOutput.write(newLineText)# close the file fnOutput.close()fn.close()